using IBApi;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Runtime.InteropServices;
using System.Text;

namespace TWSLib
{
    /**
     * @class CommissionReport
     * @brief class representing the commissions generated by an execution.
     * @sa Execution
     */
    [ComVisible(true)]
    public class ComCommissionReport : ComWrapper<CommissionReport>, ICommissionReport
    {
        /**
        * @brief the execution's id this commission belongs to.
        */
        string ExecId
        {
            get { return data != null ? data.ExecId : default(string); }
            set { if (data != null) data.ExecId = value; }
        }

        /**
         * @brief the commissions cost.
         */
        double Commission
        {
            get { return data != null ? data.Commission : default(double); }
            set { if (data != null) data.Commission = value; }
        }

        /**
        * @brief the reporting currency.
        */
        string Currency
        {
            get { return data != null ? data.Currency : default(string); }
            set { if (data != null) data.Currency = value; }
        }

        /**
        * @brief the realized profit and loss
        */
        double RealizedPNL
        {
            get { return data != null ? data.RealizedPNL : default(double); }
            set { if (data != null) data.RealizedPNL = value; }
        }

        /**
         * @brief The income return.
         */
        double Yield
        {
            get { return data != null ? data.Yield : default(double); }
            set { if (data != null) data.Yield = value; }
        }

        /**
         * @brief date expressed in yyyymmdd format.
         */
        int YieldRedemptionDate
        {
            get { return data != null ? data.YieldRedemptionDate : default(int); }
            set { if (data != null) data.YieldRedemptionDate = value; }
        }

        public override bool Equals(Object p_other)
        {
            bool l_bRetVal = false;

            if (p_other == null)
            {
                l_bRetVal = false;
            }
            else if (this == p_other)
            {
                l_bRetVal = true;
            }
            else
            {
                CommissionReport l_theOther = (CommissionReport)p_other;
                l_bRetVal = ExecId.Equals(l_theOther.ExecId);
            }
            return l_bRetVal;
        }

        string TWSLib.ICommissionReport.execId
        {
            get { return ExecId; }
        }

        double TWSLib.ICommissionReport.commission
        {
            get { return Commission; }
        }

        string TWSLib.ICommissionReport.currency
        {
            get { return Currency; }
        }

        double TWSLib.ICommissionReport.realizedPNL
        {
            get { return RealizedPNL; }
        }

        double TWSLib.ICommissionReport.yield
        {
            get { return Yield; }
        }

        int TWSLib.ICommissionReport.yieldRedemptionDate
        {
            get { return YieldRedemptionDate; }
        }

        public static explicit operator ComCommissionReport(CommissionReport cr)
        {
            return new ComCommissionReport().ConvertFrom(cr) as ComCommissionReport;
        }

        public static explicit operator CommissionReport(ComCommissionReport ccr)
        {
            return ccr.ConvertTo();
        }
    }
}
